variables.defaultValue:
(theme: ThemeVariables) => {
  return {
    root: {
      display: 'inline-block',
      position: 'relative',
      marginBottom: '1em',
      lineHeight: 1.125
    },
    container: {
      height: '100%',
      display: 'flex',
      alignItems: 'center',
      '&:after': {
        ...LY_COMMON_STYLES.fill,
        content: `\'\'`,
        pointerEvents: 'none',
        borderColor: theme.input.borderColor
      }
    },
    fieldset: {
      ...LY_COMMON_STYLES.fill,
      margin: 0,
      borderStyle: 'solid',
      borderColor: theme.input.borderColor,
      borderWidth: 0
    },
    fieldsetSpan: {
      padding: 0
    },
    labelSpan: {
      maxWidth: '100%',
      display: 'inline-block',
      transition: `font-size ${theme.animations.curves.deceleration} .${theme.animations.durations.complex}s`
    },
    prefix: {
      maxHeight: '2em',
      display: 'flex',
      alignItems: 'center',
      '&:after': {
        content: `\'\'`,
        pointerEvents: 'none',
        boxSizing: 'content-box',
        ...LY_COMMON_STYLES.fill,
        borderColor: theme.input.borderColor
      }
    },
    infix: {
      display: 'inline-flex',
      position: 'relative',
      '&:after': {
        content: `\'\'`,
        pointerEvents: 'none',
        boxSizing: 'content-box',
        ...LY_COMMON_STYLES.fill,
        borderColor: theme.input.borderColor
      }
    },
    suffix: {
      maxHeight: '2em',
      display: 'flex',
      alignItems: 'center',
      '&:after': {
        content: `\'\'`,
        pointerEvents: 'none',
        boxSizing: 'content-box',
        ...LY_COMMON_STYLES.fill,
        borderColor: theme.input.borderColor
      }
    },
    labelContainer: {
      ...LY_COMMON_STYLES.fill,
      pointerEvents: 'none',
      display: 'flex',
      width: '100%',
      borderColor: theme.input.borderColor
    },
    labelSpacingStart: {},
    labelCenter: {
      display: 'flex',
      maxWidth: '100%'
    },
    labelSpacingEnd: {
      flex: 1
    },
    label: {
      ...LY_COMMON_STYLES.fill,
      margin: 0,
      border: 'none',
      pointerEvents: 'none',
      whiteSpace: 'nowrap',
      textOverflow: 'ellipsis',
      overflow: 'hidden',
      color: theme.input.label,
      width: '100%',
      transition: `${theme.animations.curves.deceleration} .${theme.animations.durations.complex}s`
    },
    isFloatingLabel: {},
    floatingLabel: {
      '& {labelSpan}': {
        fontSize: '75%'
      }
    },
    placeholder: {
      ...LY_COMMON_STYLES.fill,
      pointerEvents: 'none',
      color: theme.input.label
    },
    focused: {},
    hint: {},
    inputNative: {
      resize: 'vertical',
      padding: 0,
      outline: 'none',
      border: 'none',
      backgroundColor: 'transparent',
      color: 'inherit',
      font: 'inherit'
    }
  };
}

