enumerationList.children:
enum ImgCropperError {
  /** The loaded image exceeds the size limit set. */
  Size,
  /** The file loaded is not image. */
  Type
}

enum ImgResolution {
  /** Resizing & cropping */
  Default,
  /** Only cropping */
  OriginalImage
}

interfaceList.children:
interface ImgCropperConfig {
  /** Set anti-aliased( default: true) */
  antiAliased: undefined | false | true,
  autoCrop: undefined | false | true,
  /** Background color( default: null), if is null in png is transparent but not in jpg. */
  fill: string | null,
  /** Cropper area height */
  height: number,
  /**
 * Emit event `error` if the file size for the limit.
 * Note: It only works when the image is received from the `<input>` event.
 */
  maxFileSize: number | null,
  output: ImgOutput | ImgResolution,
  /** If this is not defined, the new image will be automatically defined. */
  type: undefined | string,
  /** Cropper area width */
  width: number
}

interface ImgCropperErrorEvent {
  /** Cropped image data URL */
  dataURL: undefined | string,
  /** Type of error */
  error: ImgCropperError,
  height: undefined | number,
  name: string | null,
  /** Original Image data URL */
  originalDataURL: undefined | string,
  position: undefined | any,
  /** Current rotation in degrees */
  rotation: undefined | number,
  scale: undefined | number,
  /** Size of the image in bytes */
  size: undefined | number,
  /** Filetype */
  type: undefined | string,
  width: undefined | number
}

interface ImgCropperEvent {
  /** Cropped image data URL */
  dataURL: undefined | string,
  height: undefined | number,
  name: string | null,
  /** Original Image data URL */
  originalDataURL: undefined | string,
  position: undefined | any,
  /** Current rotation in degrees */
  rotation: undefined | number,
  scale: undefined | number,
  /** Size of the image in bytes */
  size: undefined | number,
  /** Filetype */
  type: undefined | string,
  width: undefined | number
}

interface ImgOutput {
  height: number,
  width: number
}

