enumerationList.children:
enum ImgResolution {
  /** Resizing & cropping */
  Default,
  /** Only cropping */
  OriginalImage
}

interfaceList.children:
interface ImgCropperConfig {
  /** Set anti-aliased( default: true) */
  antiAliased: boolean,
  autoCrop: boolean,
  /** Background color( default: null), if is null in png is transparent but not in jpg */
  fill: string | null,
  /** Cropper area height */
  height: number,
  output: any | ImgResolution,
  /** If this is not defined, the new image will be automatically defined */
  type: string,
  /** Cropper area width */
  width: number
}

interface ImgCropperEvent {
  /** Cropped image in base64, !deprecated use instead `dataURL` */
  base64: string,
  /** Cropped image data URL */
  dataURL: string,
  height: number,
  name: string,
  /** Original Image data URL */
  originalDataURL: string,
  position: any,
  /** Current rotation in degrees */
  rotation: number,
  scale: number,
  /** Filetype */
  type: string,
  width: number
}

