interfacesCode:
/** Image Cropper Config */
interface ImgCropperConfig {
  /** Set anti-aliased( default: true) */
  antiAliased?: boolean
  /** Background color( default: null), if is null in png is transparent but not in jpg */
  fill?: string | null
  /** Cropper area height */
  height: number
  output?: literal type | ImgResolution
  /** If this is not defined, the new image will be automatically defined */
  type?: string
  /** Cropper area width */
  width: number
}

interface ImgCropperEvent {
  /** Cropped image in base64 */
  base64: string
  height: number
  name: string
  /** Original Image in base64 */
  originalBase64: string
  /** Filetype */
  type: string
  width: number
}
